//       >
//       <input
//         type='file'
//         accept='.text, .json'/>
//       <input
//         type='submit'
//         value='ADD file'
//       />
//     </form>
//   )
// }
let inp = document.forms[0];

class UploadFile extends React.Component{
  addFileToList = () =>{
    fetch('/api/items', {
      method: 'POST',
      type : 'application/json',
      body: inp
    })
      .then(res => console.log(res))
  }
  render() {
    
    return (
      <form
      onSubmit={this.addFileToList}
      className='upload-form'
      >
      <input
        type='file'
        accept='.text, .json'/>
      <input
        type='submit'
        value='ADD file'
      />
    </form>
    )
  }
}

export default UploadFile
