import React from 'react'
import './style.scss'

export default (props) => {
  return (
    <form
      onSubmit={props.addFile}
      className='upload-form'
      >
      <input
        type='file'
        accept='.text, .json'/>
      <input
        type='submit'
        value='ADD file'
        onChange={(e) => onChange(e)}
      />
    </form>
  )
}
